class Synthesizer {
    constructor(audioContext) {
        this.audioCtx = new audioContext();
        this.noteTable = [...document.querySelectorAll(".piano-key")];        

        this.createNoteTable();
        this.setup();
        document.addEventListener("keypress", this.keyPressed.bind(this), false);
        document.addEventListener("keyup", this.keyReleased.bind(this), false);
        document.querySelector(".waveform-selector").addEventListener("change", this.onWaveformChange.bind(this), false);
        document.querySelector(".volume-selector").addEventListener("change", this.onVolumeChange.bind(this), false);
        document.querySelector(".frequency-selector").addEventListener("change", this.onFrequencyChange.bind(this), false);
        document.querySelector(".filter-selector").addEventListener("change", this.onFilterChange.bind(this), false);
        document.querySelector(".filter-frequency-selector").addEventListener("change", this.onFilterFrequencyChange.bind(this), false);
    }

    setup() {
        //Setting up the initial values for the oscillator 
        this.waveform = "sine";
        this.gainValue = 1.000;
        this.frequency = 440;
        this.filterFrequency = 1000;
        this.filterType = "lowpass";

        //Setting up the filter, gain and organ
        this.filter = this.audioCtx.createBiquadFilter();
        this.masterGainNode = this.audioCtx.createGain();
        this.setupFilter();
        this.setupGain();
        this.setupOrgan();

        //Setting up the connections
        this.masterGainNode.connect(this.filter);
        this.filter.connect(this.audioCtx.destination);
    }

    setupFilter() {
        this.filter.type = this.filterType;
        this.filter.frequency.setValueAtTime(this.filterFrequency, this.audioCtx.currentTime);
    }

    setupGain() {
        this.masterGainNode.gain.value = this.gainValue;
    }

    setupOrgan() {
        const c = tables.real.length;
        const real = new Float32Array(c);
        this.imag = new Float32Array(c);
        for(let i = 0; i < c; i++) {
            real[i] = tables.real[i];
            this.imag[i] = tables.imag[i];
        }

        this.organWaveform = this.audioCtx.createPeriodicWave(real, this.imag);
    }

    createNoteTable() {
        this.noteTable = this.noteTable.map((note, index) => {
            return ({
                elem: note,
                osc: null,
                index,
                frequency: () => { return this.frequency * Math.pow(2, (index / 12))},
                key: (note.querySelector("span").innerText).toLowerCase(),
                isPressed: false,
                setActive() {
                    this.elem.classList.add("active");
                },
                removeActive() {
                    this.elem.classList.remove("active");
                }
            });
        });
    }

    playNote({osc, frequency}) {        
        //Setting the oscillator
        if(this.waveform === "organ") {
            osc.setPeriodicWave(this.organWaveform, this.imag);
            this.onFrequencyChange({ target: { value: 220 } });
        } else {
            osc.type = this.waveform;
        }

        osc.frequency.setValueAtTime(frequency(), this.audioCtx.currentTime);
        osc.connect(this.masterGainNode);
        osc.start();
    }

    stopNote({ osc }) {
        osc.stop();
    }

    keyPressed({ key }) {
        const note = this.noteTable.find(n => n.key ===  key);

        if(note && !note.isPressed) {
            note.osc = this.audioCtx.createOscillator();
            note.isPressed = true;            
            note.setActive();

            this.playNote(note);
        }        
    }

    keyReleased({ key }) {
        const note = this.noteTable.find(n => n.key === key);

        if(note && note.isPressed) {
            this.stopNote(note);

            note.osc = null;
            note.isPressed = false;
            note.removeActive();    
        }        
    }

    onWaveformChange({ target }) {
        this.waveform = target.value;
    }

    onVolumeChange({ target }) {
        this.gainValue = parseFloat(target.value);
        this.setupGain();
    }

    onFrequencyChange({ target }) {
        this.frequency = Number(target.value);
    }

    onFilterChange({ target }) {
        this.filterType = target.value;
        this.setupFilter();
    }

    onFilterFrequencyChange({ target }) {
        this.filterFrequency = Number(target.value);
        this.setupFilter();
    }
}

const showOldBrowserScreen = () => {
    document.body.innerHTML = "";
    const node = document.createElement("h1");
    node.innerText = "It appears that you are using an old browser, please try another one (e.g. Chrome)";
    document.body.appendChild(node);
}

window.onload = () => {
    const audioContext = window.AudioContext || window.webkitAudioContext;
    if(audioContext) {
        new Synthesizer(audioContext);
        return;
    }

    showOldBrowserScreen();
}

